[data-bspk='avatar'] {
    --height: var(--spacing-sizing-10);
    --font: var(--labels-base);
    --svg-size: var(--spacing-sizing-10);

    &:not([data-color]) {
        --foreground: var(--foreground-neutral-on-surface);
        --background: var(--surface-neutral-t3-low);
    }

    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    padding: 0;
    height: var(--height);
    width: var(--height);
    aspect-ratio: 1 / 1;
    border-radius: 999px;
    background-color: var(--background);
    color: var(--foreground);
    font: var(--font);

    &:has(img) {
        overflow: hidden;
    }

    img {
        max-width: 100%;
    }

    svg {
        width: var(--svg-size);
        height: var(--svg-size);
    }

    [data-icon] {
        display: flex;
        align-items: center;
        justify-content: center;
    }

    &[data-size='x-small'] {
        --height: var(--spacing-sizing-06);
        --font: var(--labels-x-small);
        --svg-size: var(--spacing-sizing-04);
    }

    &[data-size='small'] {
        --height: var(--spacing-sizing-08);
