componentsMeta.css:
[data-bspk='dialog'] {
    position: fixed;
    inset: 0;
    z-index: var(--z-index-dialog);

    [data-dialog-box] {
        text-align: start;
        position: relative;
        border-radius: var(--radius-large);
        box-shadow: var(--drop-shadow-float);
        background: var(--surface-neutral-t1-base);
        color: var(--foreground-neutral-on-surface);
        max-height: calc(100vh - var(--spacing-sizing-24));
        overflow: scroll;
        z-index: 2;

        > :first-of-type {
            margin-top: 0;
        }

        > :last-child {
            margin-bottom: 0;
        }

        width: calc(100% - var(--spacing-sizing-08));

        // we make the width responsive to the viewport
        @media (width >= 640px) {
            width: 90%;
        }

        @media (width >= 768px) {
            width: 80%;
        }

        @media (width >= 1024px) {
            width: 60%;
        }

        @media (width >= 1280px) {
            width: 50%;
        }
    }

    [data-dialog-backdrop] {
        z-index: 1;
        position: fixed;
        inset: 0;
        background-color: var(--background-scrim);
        opacity: 0;
        transition: opacity 0.3s;
    }

    &[data-visibility='show'] {
        [data-dialog-backdrop] {
            opacity: 1;
        }
    }

    &[data-placement='center'] {
        display: flex;
        justify-content: center;
        align-items: center;

        [data-dialog-box] {
            transition: opacity 1s;
            opacity: 0;
        }

        &[data-visibility='show'] {
            [data-dialog-box] {
                opacity: 1;
            }
